parameters.param:
set -u source $(pwd)/build-vars-common.sh || exit 1
SPEC_FILE="stage2.spec" TEMPLATES="stage2.spec.boot.template stage2.spec.livecd-post.template"
[[ -d ${WORK_DIR} ]] || mkdir -p ${WORK_DIR}
touch ${WORK_DIR}/${SPEC_FILE} || exit 1
[[ -f ${TEMPLATE_DIR}/stage2.spec.header.template ]] || exit 1 for template in ${TEMPLATES[@]} do [[ -f ${TEMPLATE_DIR}/${template} ]] || exit 1 done
echo "Mangling templates" sed -e "s/###SUB_ARCH###/${SUB_ARCH}/" \ -e "s/###VERSION###/${BUILD_VER}/" \ -e "s/###REL_TYPE###/${REL_NAME}/" \ -e "s/###REL_PROFILE###/${REL_TYPE}\/linux\/${SUB_ARCH}/" \ -e "s/###REL_SNAPSHOT###/${SNAPSHOT}/" \ -e "s/###SRC_PATH###/${REL_NAME}\/livecd-stage1-${SUB_ARCH}-${BUILD_VER}/" \ ${TEMPLATE_DIR}/stage2.spec.header.template > ${WORK_DIR}/${SPEC_FILE}
echo >> ${WORK_DIR}/${SPEC_FILE}
sed -e "s/###SUB_ARCH###/${SUB_ARCH}/" \ -e "s/###REL_TYPE###/${REL_TYPE}/" \ -e "s/###VERSION###/${BUILD_VER}/" \ -e "s/###USERS###/${USERS}/" \ ${TEMPLATE_DIR}/stage2.spec.livecd.template >> ${WORK_DIR}/${SPEC_FILE}
echo "Processing template headers" for template in ${TEMPLATES[@]} do echo >> ${WORK_DIR}/${SPEC_FILE} cat ${TEMPLATE_DIR}/$template >> ${WORK_DIR}/${SPEC_FILE} || exit 1 done
echo "Running catalyst scripts" cd ${WORK_DIR} sudo /usr/bin/catalyst -f ${SPEC_FILE}
