parameters.param:
(( %param.stage2.skip% == 1 )) && exit 0
set -u source $(pwd)/build-vars-common.sh || exit 1
SPEC_FILE="stage2.spec" TEMPLATES="stage2.spec.boot.template stage2.spec.livecd-post.template"
[[ -d ${WORK_DIR} ]] || mkdir -p ${WORK_DIR}
touch ${WORK_DIR}/${SPEC_FILE} || exit 1
[[ -f ${TEMPLATE_DIR}/stage2.spec.header.template ]] || exit 1 for template in ${TEMPLATES[@]} do [[ -f ${TEMPLATE_DIR}/${template} ]] || exit 1 done
echo "Mangling templates" sed -e "s/###SUB_ARCH###/${SUB_ARCH}/" \ -e "s/###BUILD_VER###/${BUILD_VER}/" \ -e "s/###REL_TYPE###/${REL_NAME}/" \ -e "s/###REL_PROFILE###/${REL_TYPE}\/linux\/${SUB_ARCH}/" \ -e "s/###REL_SNAPSHOT###/${SNAPSHOT}/" \ -e "s/###SRC_PATH###/${REL_NAME}\/livecd-stage1-${SUB_ARCH}-${BUILD_VER}/" \ ${TEMPLATE_DIR}/stage2.spec.header.template > ${WORK_DIR}/${SPEC_FILE}
echo >> ${WORK_DIR}/${SPEC_FILE}
sed -e "s/###SUB_ARCH###/${SUB_ARCH}/" \ -e "s/###REL_TYPE###/${REL_TYPE}/" \ -e "s/###REL_NAME###/${REL_NAME}/g" \ -e "s/###BUILD_VER###/${BUILD_VER}/" \ -e "s/###USERS###/${USERS}/" \ ${TEMPLATE_DIR}/stage2.spec.livecd.template >> ${WORK_DIR}/${SPEC_FILE}
sed -e "s/###BUILD_BUILDER###/${BUILD_BUILDER}/g"\ -e "s/###TARGET_HOST###/${TARGET_HOST}/g" /etc/catalyst/templates/common/wrangle.sh.template > /etc/catalyst/scripts/${REL_NAME}/wrangle.sh
echo "Processing template headers" for template in ${TEMPLATES[@]} do echo >> ${WORK_DIR}/${SPEC_FILE} cat ${TEMPLATE_DIR}/$template >> ${WORK_DIR}/${SPEC_FILE} || exit 1 done
echo "Running catalyst scripts" cd ${WORK_DIR} if (( STAGE_RESUME == 0 )) then echo "Clearing resume flags..." sudo /usr/bin/catalyst -a -f ${SPEC_FILE} || exit 1 else echo "Resuming if able..." sudo /usr/bin/catalyst -f ${SPEC_FILE} || exit 1 fi [[ -f /var/data/catalyst/builds/${REL_NAME}/${REL_NAME}-${SUB_ARCH}-${REL_TYPE}-installer-${BUILD_VER}.iso ]] && mv /var/data/catalyst/builds/${REL_NAME}/${REL_NAME}-${SUB_ARCH}-${REL_TYPE}-installer-${BUILD_VER}.iso* /var/data/images/iso/
echo "Fin."
